import { useState } from 'react';
import Button from '@material-ui/core/Button';
import FormControl from '@material-ui/core/FormControl';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Paper from '@material-ui/core/Paper';
import Radio from '@material-ui/core/Radio';
import RadioGroup from '@material-ui/core/RadioGroup';
import styled from 'styled-components';
import TextField from '@material-ui/core/TextField';
import Typography from '@material-ui/core/Typography';
import { useFormik } from 'formik';
import * as yup from 'yup';

import { useStyles } from '../hooks';

const Wrapper = styled.section`
  display: flex;
  flex-direction: column;
`;

const Row = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  width: 100%;
  padding: 1em;
`;

const StyledFormControl = styled(FormControl)`
  min-width: 120px;
`;

const ContentPaper = styled(Paper)`
  height: 300px;
  padding: 2em;
  overflow: auto;
`;

const FormSchema = yup.object().shape({
  name: yup.string().required(),
  subject: yup.string().required(),
  score: yup.number().required(),
});

const Body = () => {
  const classes = useStyles();

  const [queryType, setQueryType] = useState('name');
  const [queryString, setQueryString] = useState('');
  const [messages, setMessages] = useState([]);

  const {
    values,
    touched,
    errors,
    handleSubmit,
    handleChange,
    handleBlur,
  } = useFormik({
    initialValues: { name: '', subject: '', score: 0 },
    onSubmit: (values) => {
      console.log(values);
    },
    validationSchema: FormSchema,
  });

  const handleGeneralChanges = (func) => (event) => {
    func(event.target.value);
  };

  const isNameErroneous = touched.name && errors.name;
  const isSubjectErroneous = touched.subject && errors.subject;
  const isScoreErroneous = touched.score && errors.score;

  return (
    <Wrapper>
      <Row>
        <form>
          <TextField
            className={classes.input}
            placeholder="Name"
            value={values.name}
            onChange={handleChange}
            onBlur={handleBlur}
            error={isNameErroneous}
            helperText={!isNameErroneous || errors.name}
          />
          <TextField
            className={classes.input}
            placeholder="Subject"
            style={{ width: 240 }}
            value={values.subject}
            onChange={handleChange}
            onBlur={handleBlur}
            error={isSubjectErroneous}
            helperText={!isSubjectErroneous || errors.subject}
          />
          <TextField
            className={classes.input}
            placeholder="Score"
            type="number"
            value={values.score}
            onChange={handleChange}
            onBlur={handleBlur}
            error={isScoreErroneous}
            helperText={errors.score}
          />
          <Button
            className={classes.button}
            variant="contained"
            color="primary"
            type="submit"
          >
            Add
          </Button>
        </form>
      </Row>
      <Row>
        <StyledFormControl>
          <FormControl component="fieldset">
            <RadioGroup
              row
              value={queryType}
              onChange={handleGeneralChanges(setQueryType)}
            >
              <FormControlLabel value="name" control={<Radio />} label="Name" />
              <FormControlLabel
                value="subject"
                control={<Radio />}
                label="Subject"
              />
            </RadioGroup>
          </FormControl>
        </StyledFormControl>
        <TextField
          placeholder="Query string..."
          value={queryString}
          onChange={handleGeneralChanges(setQueryString)}
          style={{ flex: 1 }}
        />
        <Button className={classes.button} variant="contained" color="primary">
          Query
        </Button>
      </Row>
      <ContentPaper variant="outlined">
        {messages.map((m) => (
          <Typography variant="body2">{m}</Typography>
        ))}
      </ContentPaper>
    </Wrapper>
  );
};

export default Body;
